import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageCircle, 
  Send, 
  AtSign, 
  Lock, 
  Eye,
  Clock,
  User
} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { RequestComment, CommentFormData, UserMention } from '../types/withdrawalTypes';
import { commentService } from '../services/commentService';
import { timelineService } from '../services/timelineService';
import UserTagging from './UserTagging';

interface CommentSystemProps {
  requestId: string;
  comments: RequestComment[];
  onCommentAdded: () => void;
}

const CommentSystem: React.FC<CommentSystemProps> = ({
  requestId,
  comments,
  onCommentAdded
}) => {
  const { user } = useAuth();
  const [commentText, setCommentText] = useState('');
  const [isInternal, setIsInternal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showUserTagging, setShowUserTagging] = useState(false);
  const [cursorPosition, setCursorPosition] = useState(0);
  const [mentionedUsers, setMentionedUsers] = useState<string[]>([]);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Handle @ symbol for user tagging
  const handleTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const value = e.target.value;
    const position = e.target.selectionStart;

    setCommentText(value);
    setCursorPosition(position);

    // Check if user typed @ symbol
    const lastChar = value[position - 1];
    if (lastChar === '@') {
      setShowUserTagging(true);
    } else if (showUserTagging && (lastChar === ' ' || lastChar === '\n')) {
      setShowUserTagging(false);
    }

    // Parse mentions from text
    const mentions = commentService.parseMentions(value);
    setMentionedUsers(mentions);
  };

  // Handle user selection from tagging component
  const handleUserSelect = (userMention: UserMention) => {
    if (!textareaRef.current) return;

    const textarea = textareaRef.current;
    const value = commentText;

    // Find the @ symbol position
    let atPosition = cursorPosition - 1;
    while (atPosition >= 0 && value[atPosition] !== '@') {
      atPosition--;
    }

    if (atPosition >= 0) {
      const beforeAt = value.substring(0, atPosition);
      const afterCursor = value.substring(cursorPosition);
      const newValue = `${beforeAt}@${userMention.name} ${afterCursor}`;

      setCommentText(newValue);
      setShowUserTagging(false);

      // Add user to mentioned users if not already included
      if (!mentionedUsers.includes(userMention.id)) {
        setMentionedUsers(prev => [...prev, userMention.id]);
      }

      // Focus back to textarea
      setTimeout(() => {
        textarea.focus();
        const newPosition = beforeAt.length + userMention.name.length + 2;
        textarea.setSelectionRange(newPosition, newPosition);
      }, 0);
    }
  };

  // Submit comment
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!commentText.trim() || !user || isSubmitting) return;

    setIsSubmitting(true);

    try {
      const commentData: CommentFormData = {
        commentText: commentText.trim(),
        mentionedUsers,
        isInternal
      };

      const newComment = commentService.addComment(requestId, user.email, commentData);

      if (newComment) {
        // Add timeline event
        timelineService.addCommentEvent(requestId, user.email, commentText.trim());

        // Reset form
        setCommentText('');
        setMentionedUsers([]);
        setIsInternal(false);

        // Notify parent component
        onCommentAdded();
      }
    } catch (error) {
      console.error('Error adding comment:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const canAddComments = user && (
    user.can_create_requests || 
    user.can_approve_reject || 
    user.can_disburse || 
    !user.view_only_access
  );

  return (
    <div className="space-y-6">
      {/* Comment Form */}
      {canAddComments && (
        <motion.div
          className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-2xl p-6 border border-blue-200 shadow-sm"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
          whileHover={{ scale: 1.01 }}
        >
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="relative">
              <textarea
                ref={textareaRef}
                value={commentText}
                onChange={handleTextChange}
                placeholder="Add a comment... Use @username to mention team members"
                className="w-full p-4 border border-gray-300 rounded-xl resize-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 bg-white shadow-sm transition-all duration-200"
                rows={4}
                disabled={isSubmitting}
                style={{ minHeight: '100px' }}
              />

              {/* User Tagging Dropdown */}
              {showUserTagging && (
                <div className="absolute top-full left-0 right-0 z-10 mt-1">
                  <UserTagging
                    onUserSelect={handleUserSelect}
                    onClose={() => setShowUserTagging(false)}
                  />
                </div>
              )}
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={isInternal}
                    onChange={(e) => setIsInternal(e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                    disabled={isSubmitting}
                  />
                  <span className="text-sm text-gray-600 flex items-center space-x-1">
                    <Lock className="w-4 h-4" />
                    <span>Internal comment</span>
                  </span>
                </label>

                {mentionedUsers.length > 0 && (
                  <div className="flex items-center space-x-1 text-sm text-blue-600">
                    <AtSign className="w-4 h-4" />
                    <span>{mentionedUsers.length} user(s) mentioned</span>
                  </div>
                )}
              </div>

              <motion.button
                type="submit"
                disabled={!commentText.trim() || isSubmitting}
                className="flex items-center space-x-2 px-6 py-3 bg-gradient-to-r from-green-600 to-green-700 text-white rounded-xl font-semibold disabled:opacity-50 disabled:cursor-not-allowed hover:from-green-700 hover:to-green-800 transition-all duration-200 shadow-lg hover:shadow-xl"
                whileHover={{ scale: 1.02, y: -1 }}
                whileTap={{ scale: 0.98 }}
              >
                {isSubmitting ? (
                  <>
                    <motion.div
                      className="w-4 h-4 border-2 border-white border-t-transparent rounded-full"
                      animate={{ rotate: 360 }}
                      transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    />
                    <span>Posting...</span>
                  </>
                ) : (
                  <>
                    <Send className="w-4 h-4" />
                    <span>Post Comment</span>
                  </>
                )}
              </motion.button>
            </div>
          </form>
        </motion.div>
      )}

      {/* Comments List */}
      <div className="space-y-4">
        <h3 className="text-lg font-semibold flex items-center space-x-2" style={{ color: '#323E48' }}>
          <MessageCircle className="w-5 h-5" />
          <span>Comments ({comments.length})</span>
        </h3>

        {comments.length === 0 ? (
          <div className="text-center py-12">
            <MessageCircle className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h4 className="text-lg font-medium text-gray-500 mb-2">No comments yet</h4>
            <p className="text-gray-400">
              {canAddComments 
                ? "Be the first to add a comment to this request."
                : "Comments will appear here when team members add them."
              }
            </p>
          </div>
        ) : (
          <AnimatePresence>
            {comments.map((comment, index) => (
              <motion.div
                key={comment.id}
                className={`bg-white rounded-2xl p-5 border-2 shadow-sm hover:shadow-md transition-all duration-300 ${
                  comment.isInternal
                    ? 'border-yellow-200 bg-gradient-to-br from-yellow-50 to-amber-50'
                    : 'border-gray-200 hover:border-blue-200'
                }`}
                initial={{ opacity: 0, y: 20, scale: 0.95 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                transition={{ duration: 0.4, delay: index * 0.1, ease: [0.16, 1, 0.3, 1] }}
                whileHover={{ y: -2, scale: 1.01 }}
              >
                <div className="flex items-start space-x-3">
                  <motion.div
                    className="flex-shrink-0"
                    whileHover={{ scale: 1.1 }}
                    transition={{ duration: 0.2 }}
                  >
                    <div className="w-12 h-12 rounded-full bg-gradient-to-br from-blue-100 to-blue-200 flex items-center justify-center text-lg shadow-md border-2 border-white">
                      {comment.userAvatar || <User className="w-6 h-6 text-blue-600" />}
                    </div>
                  </motion.div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2 mb-1">
                      <h4 className="font-medium text-gray-900">{comment.userName}</h4>
                      {comment.isInternal && (
                        <span className="inline-flex items-center space-x-1 px-2 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">
                          <Lock className="w-3 h-3" />
                          <span>Internal</span>
                        </span>
                      )}
                      <span className="text-sm text-gray-500 flex items-center space-x-1">
                        <Clock className="w-3 h-3" />
                        <span>{formatDate(comment.createdAt)}</span>
                      </span>
                    </div>

                    <div className="text-gray-700 whitespace-pre-wrap">
                      {comment.commentText}
                    </div>

                    {comment.mentionedUsers && comment.mentionedUsers.length > 0 && (
                      <div className="mt-2 flex items-center space-x-1 text-sm text-blue-600">
                        <AtSign className="w-3 h-3" />
                        <span>{comment.mentionedUsers.length} user(s) mentioned</span>
                      </div>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        )}
      </div>
    </div>
  );
};

export default CommentSystem;
