import React from 'react';
import { motion } from 'framer-motion';
import { 
  Clock, 
  CheckCircle, 
  AlertTriangle, 
  TrendingUp, 
  Calendar,
  Target,
  Zap,
  BarChart3,
  Timer,
  ArrowRight
} from 'lucide-react';
import { WithdrawalRequest } from '../types/withdrawalTypes';

interface RequestTrackingDashboardProps {
  request: WithdrawalRequest;
}

const RequestTrackingDashboard: React.FC<RequestTrackingDashboardProps> = ({ request }) => {
  // Define the 4-stage workflow
  const workflowStages = [
    {
      id: 'initial_review',
      name: 'Initial Review',
      description: 'Document verification and basic validation',
      icon: Clock,
      color: 'blue',
      estimatedDays: 1
    },
    {
      id: 'technical_review',
      name: 'Technical Review',
      description: 'Detailed technical assessment and approval',
      icon: Target,
      color: 'purple',
      estimatedDays: 3
    },
    {
      id: 'core_banking',
      name: 'Core Banking',
      description: 'Financial processing and disbursement preparation',
      icon: BarChart3,
      color: 'orange',
      estimatedDays: 2
    },
    {
      id: 'disbursed',
      name: 'Disbursed',
      description: 'Funds successfully transferred to beneficiary',
      icon: CheckCircle,
      color: 'green',
      estimatedDays: 0
    }
  ];

  // Get current stage index
  const currentStageIndex = workflowStages.findIndex(stage => stage.id === request.currentStage);
  const isCompleted = request.currentStage === 'disbursed';

  // Calculate progress percentage
  const progressPercentage = isCompleted ? 100 : ((currentStageIndex + 1) / workflowStages.length) * 100;

  // Calculate estimated completion
  const remainingDays = workflowStages
    .slice(currentStageIndex + 1)
    .reduce((total, stage) => total + stage.estimatedDays, 0);

  // Calculate if overdue
  const createdDate = new Date(request.createdAt);
  const currentDate = new Date();
  const daysSinceCreated = Math.floor((currentDate.getTime() - createdDate.getTime()) / (1000 * 60 * 60 * 24));
  const totalEstimatedDays = workflowStages.reduce((total, stage) => total + stage.estimatedDays, 0);
  const isOverdue = daysSinceCreated > totalEstimatedDays && !isCompleted;

  const getStageStatus = (stageIndex: number) => {
    if (stageIndex < currentStageIndex) return 'completed';
    if (stageIndex === currentStageIndex) return 'current';
    return 'pending';
  };

  const getStageColor = (stage: any, status: string) => {
    if (status === 'completed') return 'bg-green-500 text-white border-green-500';
    if (status === 'current') {
      switch (stage.color) {
        case 'blue': return 'bg-blue-500 text-white border-blue-500';
        case 'purple': return 'bg-purple-500 text-white border-purple-500';
        case 'orange': return 'bg-orange-500 text-white border-orange-500';
        case 'green': return 'bg-green-500 text-white border-green-500';
        default: return 'bg-gray-500 text-white border-gray-500';
      }
    }
    return 'bg-gray-100 text-gray-500 border-gray-300';
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <motion.div
        className="flex items-center justify-between"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
      >
        <div className="flex items-center space-x-3">
          <div className="p-2 rounded-xl bg-gradient-to-r from-blue-500 to-purple-600 text-white">
            <TrendingUp className="w-5 h-5" />
          </div>
          <div>
            <h3 className="text-lg font-bold text-gray-900">Request Tracking</h3>
            <p className="text-sm text-gray-600">Monitor progress through the 4-stage workflow</p>
          </div>
        </div>

        <div className="text-right">
          <div className="text-2xl font-bold text-gray-900">{Math.round(progressPercentage)}%</div>
          <div className="text-sm text-gray-500">Complete</div>
        </div>
      </motion.div>

      {/* Progress Bar */}
      <motion.div
        className="relative"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 0.1, duration: 0.3 }}
      >
        <div className="h-3 bg-gray-200 rounded-full overflow-hidden">
          <motion.div
            className="h-full bg-gradient-to-r from-blue-500 via-purple-500 to-green-500 rounded-full"
            initial={{ width: 0 }}
            animate={{ width: `${progressPercentage}%` }}
            transition={{ delay: 0.5, duration: 1, ease: "easeOut" }}
          />
        </div>
      </motion.div>

      {/* Workflow Stages */}
      <div className="space-y-4">
        {workflowStages.map((stage, index) => {
          const status = getStageStatus(index);
          const IconComponent = stage.icon;

          return (
            <motion.div
              key={stage.id}
              className={`relative flex items-center space-x-4 p-4 rounded-2xl border-2 transition-all duration-300 ${
                status === 'completed' ? 'bg-green-50 border-green-200' :
                status === 'current' ? 'bg-blue-50 border-blue-200 shadow-lg' :
                'bg-gray-50 border-gray-200'
              }`}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.2 + index * 0.1, duration: 0.3 }}
              whileHover={{ scale: 1.02, y: -2 }}
            >
              {/* Stage Icon */}
              <div className={`flex items-center justify-center w-12 h-12 rounded-xl border-2 ${getStageColor(stage, status)}`}>
                <IconComponent className="w-6 h-6" />
              </div>

              {/* Stage Content */}
              <div className="flex-1">
                <div className="flex items-center space-x-2 mb-1">
                  <h4 className="font-semibold text-gray-900">{stage.name}</h4>
                  {status === 'completed' && (
                    <CheckCircle className="w-4 h-4 text-green-600" />
                  )}
                  {status === 'current' && (
                    <motion.div
                      className="w-2 h-2 bg-blue-500 rounded-full"
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ duration: 1, repeat: Infinity }}
                    />
                  )}
                </div>
                <p className="text-sm text-gray-600">{stage.description}</p>
              </div>

              {/* Estimated Time */}
              <div className="text-right">
                <div className="text-sm font-medium text-gray-900">
                  {stage.estimatedDays > 0 ? `${stage.estimatedDays} day${stage.estimatedDays > 1 ? 's' : ''}` : 'Complete'}
                </div>
                <div className="text-xs text-gray-500">
                  {status === 'completed' ? 'Completed' :
                   status === 'current' ? 'In Progress' :
                   'Pending'}
                </div>
              </div>

              {/* Connection Line */}
              {index < workflowStages.length - 1 && (
                <div className="absolute left-6 top-16 w-0.5 h-6 bg-gray-300"></div>
              )}
            </motion.div>
          );
        })}
      </div>

      {/* SLA Tracking */}
      <motion.div
        className={`p-4 rounded-2xl border-2 ${
          isOverdue ? 'bg-red-50 border-red-200' : 'bg-green-50 border-green-200'
        }`}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.6, duration: 0.3 }}
      >
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className={`p-2 rounded-xl ${isOverdue ? 'bg-red-500' : 'bg-green-500'} text-white`}>
              {isOverdue ? <AlertTriangle className="w-5 h-5" /> : <Timer className="w-5 h-5" />}
            </div>
            <div>
              <h4 className="font-semibold text-gray-900">SLA Status</h4>
              <p className="text-sm text-gray-600">
                {isOverdue 
                  ? `Overdue by ${daysSinceCreated - totalEstimatedDays} day(s)`
                  : isCompleted 
                    ? 'Completed within SLA'
                    : `${remainingDays} day(s) remaining`
                }
              </p>
            </div>
          </div>

          <div className="text-right">
            <div className="text-lg font-bold text-gray-900">{daysSinceCreated} days</div>
            <div className="text-sm text-gray-500">Since created</div>
          </div>
        </div>
      </motion.div>

      {/* Quick Stats */}
      <motion.div
        className="grid grid-cols-3 gap-4"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7, duration: 0.3 }}
      >
        <div className="bg-white p-4 rounded-xl border border-gray-200 text-center">
          <Calendar className="w-6 h-6 text-blue-500 mx-auto mb-2" />
          <div className="text-lg font-bold text-gray-900">{daysSinceCreated}</div>
          <div className="text-xs text-gray-500">Days Active</div>
        </div>

        <div className="bg-white p-4 rounded-xl border border-gray-200 text-center">
          <Zap className="w-6 h-6 text-purple-500 mx-auto mb-2" />
          <div className="text-lg font-bold text-gray-900">{currentStageIndex + 1}/4</div>
          <div className="text-xs text-gray-500">Stages Complete</div>
        </div>

        <div className="bg-white p-4 rounded-xl border border-gray-200 text-center">
          <Target className="w-6 h-6 text-green-500 mx-auto mb-2" />
          <div className="text-lg font-bold text-gray-900">{remainingDays}</div>
          <div className="text-xs text-gray-500">Days Remaining</div>
        </div>
      </motion.div>
    </div>
  );
};

export default RequestTrackingDashboard;
