import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  X, 
  Calendar, 
  DollarSign, 
  MapPin, 
  User, 
  Clock, 
  MessageCircle, 
  FileText, 
  Activity,
  Eye,
  AlertCircle,
  CheckCircle,
  XCircle,
  Pause
} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { WithdrawalRequest, RequestDetails, RequestDetailsModalState } from '../types/withdrawalTypes';
import { withdrawalRequestService } from '../services/withdrawalRequestService';
import { commentService } from '../services/commentService';
import { timelineService } from '../services/timelineService';
import CommentSystem from './CommentSystem';
import TimelineComponent from './TimelineComponent';

interface RequestDetailsModalProps {
  isOpen: boolean;
  requestId: string | null;
  onClose: () => void;
}

const RequestDetailsModal: React.FC<RequestDetailsModalProps> = ({
  isOpen,
  requestId,
  onClose
}) => {
  const { user } = useAuth();
  const [modalState, setModalState] = useState<RequestDetailsModalState>({
    isOpen: false,
    requestId: null,
    activeTab: 'overview',
    isLoading: false
  });
  const [requestDetails, setRequestDetails] = useState<RequestDetails | null>(null);

  // Load request details when modal opens
  useEffect(() => {
    if (isOpen && requestId) {
      setModalState(prev => ({ ...prev, isLoading: true }));
      loadRequestDetails(requestId);
    }
  }, [isOpen, requestId]);

  const loadRequestDetails = async (id: string) => {
    try {
      const request = withdrawalRequestService.getRequestById(id);
      if (!request) {
        console.error('Request not found:', id);
        return;
      }

      const comments = commentService.getCommentsByRequestId(id);
      const timeline = timelineService.getTimelineByRequestId(id);
      const timelineStats = timelineService.getTimelineStats(id);

      const details: RequestDetails = {
        ...request,
        comments,
        timeline,
        documents: [], // TODO: Implement document service
        totalComments: comments.length,
        lastActivity: timelineStats.lastActivity || request.updatedAt
      };

      setRequestDetails(details);
    } catch (error) {
      console.error('Error loading request details:', error);
    } finally {
      setModalState(prev => ({ ...prev, isLoading: false }));
    }
  };

  const handleClose = () => {
    setRequestDetails(null);
    setModalState({
      isOpen: false,
      requestId: null,
      activeTab: 'overview',
      isLoading: false
    });
    onClose();
  };

  const handleTabChange = (tab: RequestDetailsModalState['activeTab']) => {
    setModalState(prev => ({ ...prev, activeTab: tab }));
  };

  const getStatusIcon = (status: string) => {
    switch (status.toLowerCase()) {
      case 'disbursed':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case 'rejected':
        return <XCircle className="w-5 h-5 text-red-600" />;
      case 'pending':
        return <Clock className="w-5 h-5 text-yellow-600" />;
      default:
        return <Pause className="w-5 h-5 text-blue-600" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'disbursed':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'rejected':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      default:
        return 'bg-blue-100 text-blue-800 border-blue-200';
    }
  };

  const formatCurrency = (amount: number, currency: string) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!isOpen || !requestDetails) {
    return null;
  }

  return (
    <AnimatePresence>
      <motion.div
        className="fixed inset-0 bg-black/60 backdrop-blur-md flex items-start justify-center p-4 pt-8 z-50"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        onClick={(e) => {
          if (e.target === e.currentTarget) {
            handleClose();
          }
        }}
        style={{ overflow: 'hidden' }}
      >
        <motion.div
          className="bg-white rounded-3xl shadow-2xl w-full max-w-6xl max-h-[90vh] overflow-hidden"
          style={{ border: '1px solid #DEE1E3' }}
          initial={{ opacity: 0, y: 50, scale: 0.95 }}
          animate={{ opacity: 1, y: 0, scale: 1 }}
          exit={{ opacity: 0, y: 50, scale: 0.95 }}
          transition={{ duration: 0.3, ease: "easeOut" }}
        >
          {/* Header */}
          <div className="px-6 py-4 border-b flex items-center justify-between" style={{ backgroundColor: '#F9F9F9', borderColor: '#DEE1E3' }}>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                {getStatusIcon(requestDetails.status)}
                <h2 className="text-xl font-bold" style={{ color: '#323E48' }}>
                  Request Details - {requestDetails.refNumber}
                </h2>
              </div>
              <div className={`px-3 py-1 rounded-full text-sm font-medium border ${getStatusColor(requestDetails.status)}`}>
                {requestDetails.status}
              </div>
            </div>
            <motion.button
              onClick={handleClose}
              className="p-2 rounded-full hover:bg-red-100 transition-colors"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <X className="w-6 h-6 text-red-600" />
            </motion.button>
          </div>

          {/* Tab Navigation */}
          <div className="px-6 py-3 border-b" style={{ backgroundColor: '#FAFAFA', borderColor: '#DEE1E3' }}>
            <div className="flex space-x-6">
              {[
                { id: 'overview', label: 'Overview', icon: Eye },
                { id: 'timeline', label: 'Timeline', icon: Activity },
                { id: 'comments', label: `Comments (${requestDetails.totalComments})`, icon: MessageCircle },
                { id: 'documents', label: 'Documents', icon: FileText }
              ].map((tab) => {
                const IconComponent = tab.icon;
                const isActive = modalState.activeTab === tab.id;

                return (
                  <motion.button
                    key={tab.id}
                    onClick={() => handleTabChange(tab.id as any)}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-xl font-medium transition-all ${
                      isActive 
                        ? 'bg-white text-blue-600 shadow-sm border border-blue-200' 
                        : 'text-gray-600 hover:text-blue-600 hover:bg-white/50'
                    }`}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <IconComponent className="w-4 h-4" />
                    <span>{tab.label}</span>
                  </motion.button>
                );
              })}
            </div>
          </div>

          {/* Content Area */}
          <div className="flex-1 overflow-y-auto" style={{ maxHeight: 'calc(90vh - 200px)' }}>
            {modalState.isLoading ? (
              <div className="flex items-center justify-center h-64">
                <motion.div
                  className="w-8 h-8 border-4 border-blue-200 border-t-blue-600 rounded-full"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                />
              </div>
            ) : (
              <div className="p-6">
                {modalState.activeTab === 'overview' && (
                  <div className="space-y-6">
                    {/* Request Overview */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div className="space-y-4">
                        <h3 className="text-lg font-semibold" style={{ color: '#323E48' }}>Request Information</h3>
                        <div className="space-y-3">
                          <div className="flex items-center space-x-3">
                            <User className="w-5 h-5 text-gray-500" />
                            <div>
                              <p className="text-sm text-gray-500">Beneficiary</p>
                              <p className="font-medium">{requestDetails.beneficiaryName}</p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-3">
                            <MapPin className="w-5 h-5 text-gray-500" />
                            <div>
                              <p className="text-sm text-gray-500">Country</p>
                              <p className="font-medium">{requestDetails.country}</p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-3">
                            <DollarSign className="w-5 h-5 text-gray-500" />
                            <div>
                              <p className="text-sm text-gray-500">Amount</p>
                              <p className="font-medium text-lg">{formatCurrency(requestDetails.amount, requestDetails.currency)}</p>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="space-y-4">
                        <h3 className="text-lg font-semibold" style={{ color: '#323E48' }}>Processing Details</h3>
                        <div className="space-y-3">
                          <div className="flex items-center space-x-3">
                            <Calendar className="w-5 h-5 text-gray-500" />
                            <div>
                              <p className="text-sm text-gray-500">Value Date</p>
                              <p className="font-medium">{formatDate(requestDetails.valueDate)}</p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-3">
                            <Clock className="w-5 h-5 text-gray-500" />
                            <div>
                              <p className="text-sm text-gray-500">Processing Days</p>
                              <p className="font-medium">{requestDetails.processingDays} days</p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-3">
                            <AlertCircle className="w-5 h-5 text-gray-500" />
                            <div>
                              <p className="text-sm text-gray-500">Priority</p>
                              <p className={`font-medium capitalize ${
                                requestDetails.priority === 'urgent' ? 'text-red-600' :
                                requestDetails.priority === 'high' ? 'text-orange-600' :
                                requestDetails.priority === 'medium' ? 'text-yellow-600' :
                                'text-green-600'
                              }`}>
                                {requestDetails.priority}
                              </p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Notes Section */}
                    {requestDetails.notes && (
                      <div className="bg-gray-50 rounded-2xl p-4">
                        <h4 className="font-medium mb-2" style={{ color: '#323E48' }}>Notes</h4>
                        <p className="text-gray-700">{requestDetails.notes}</p>
                      </div>
                    )}
                  </div>
                )}

                {modalState.activeTab === 'timeline' && (
                  <TimelineComponent 
                    requestId={requestDetails.id} 
                    timeline={requestDetails.timeline}
                  />
                )}

                {modalState.activeTab === 'comments' && (
                  <CommentSystem 
                    requestId={requestDetails.id}
                    comments={requestDetails.comments}
                    onCommentAdded={() => loadRequestDetails(requestDetails.id)}
                  />
                )}

                {modalState.activeTab === 'documents' && (
                  <div className="text-center py-12">
                    <FileText className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-500 mb-2">Document Management</h3>
                    <p className="text-gray-400">Document upload and management functionality will be implemented in the next phase.</p>
                  </div>
                )}
              </div>
            )}
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default RequestDetailsModal;
