import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { AuditTrailService } from '../lib/auditTrail';
import ChangePassword from './ChangePassword';
import {
  TrendingUp,
  Users,
  Clock,
  CheckCircle,
  AlertTriangle,
  DollarSign,
  Activity,
  Shield,
  Database,
  Zap,
  ArrowRight,
  Star,
  Award,
  Target,
  BarChart3,
  PieChart,
  Calendar,
  Bell,
  Eye,
  Edit,
  CreditCard,
  Globe
} from 'lucide-react';

// Animation variants
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.2
    }
  }
};

const cardHoverVariants = {
  scale: 1.02,
  y: -5,
  transition: {
    duration: 0.2
  }
};

const Dashboard: React.FC = () => {
  const { user, signOut, loading } = useAuth();
  const navigate = useNavigate();
  const [mounted, setMounted] = useState(false);
  const [showChangePassword, setShowChangePassword] = useState(false);

  useEffect(() => {
    setMounted(true);

    // Log dashboard access
    if (user) {
      AuditTrailService.logDataAccess('dashboard', 'view', 'main_dashboard', {
        user_role: user.role,
        access_timestamp: new Date().toISOString()
      });
    }
  }, [user]);

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!loading && !user) {
      console.log('🔄 User not authenticated, redirecting to login...');
      navigate('/login', { replace: true });
    }
  }, [user, loading, navigate]);

  const handleSignOut = async () => {
    try {
      console.log('🚪 Sign out button clicked - starting sign out process...');

      // Log the logout action
      AuditTrailService.logUserActivity('logout', 'User initiated logout from dashboard');

      // Sign out from Supabase (this will handle audit trail cleanup)
      await signOut();

      console.log('✅ User signed out successfully from Supabase');

      // Force navigation to login page
      navigate('/login', { replace: true });

      console.log('🔄 Navigation to login page initiated');

    } catch (error) {
      console.error('❌ Error signing out:', error);
      AuditTrailService.logError('logout_error', 'Failed to sign out from dashboard', error instanceof Error ? error.stack : undefined);
      // Even if there's an error, still try to navigate to login
      navigate('/login', { replace: true });
    }
  };

  const getRoleDisplayName = (role: string) => {
    const roleMap: { [key: string]: string } = {
      'admin': 'System Administrator',
      'archive_team': 'Archive Team Member',
      'loan_administrator': 'Loan Administrator',
      'head_of_operations': 'Head of Operations',
      'operations_team': 'Operations Team Lead',
      'core_banking': 'Core Banking Team Member'
    };
    return roleMap[role] || role;
  };

  const getRolePermissions = (user: any) => {
    const permissions = [];
    if (user?.can_create_requests) permissions.push('Create Requests');
    if (user?.can_approve_reject) permissions.push('Approve/Reject');
    if (user?.can_disburse) permissions.push('Disburse Funds');
    if (user?.view_only_access) permissions.push('View Only');
    return permissions;
  };

  const getRoleColor = (role: string) => {
    const colorMap: { [key: string]: string } = {
      'admin': 'bg-purple-100 text-purple-800 border-purple-200',
      'archive_team': 'bg-blue-100 text-blue-800 border-blue-200',
      'loan_administrator': 'bg-green-100 text-green-800 border-green-200',
      'head_of_operations': 'bg-orange-100 text-orange-800 border-orange-200',
      'operations_team': 'bg-indigo-100 text-indigo-800 border-indigo-200',
      'core_banking': 'bg-red-100 text-red-800 border-red-200'
    };
    return colorMap[role] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getRoleGradient = (role: string) => {
    switch (role) {
      case 'admin':
        return 'from-purple-500 to-pink-500';
      case 'archive_team':
        return 'from-blue-500 to-cyan-500';
      case 'loan_administrator':
        return 'from-green-500 to-emerald-500';
      case 'head_of_operations':
