import React from 'react';
import { motion } from 'framer-motion';
import { 
  Clock, 
  CheckCircle, 
  XCircle, 
  AlertCircle, 
  MessageCircle, 
  FileText, 
  UserCheck, 
  Plus,
  ArrowRight,
  DollarSign,
  User
} from 'lucide-react';
import { TimelineEvent } from '../types/withdrawalTypes';

interface TimelineComponentProps {
  requestId: string;
  timeline: TimelineEvent[];
}

const TimelineComponent: React.FC<TimelineComponentProps> = ({
  requestId,
  timeline
}) => {
  const getEventIcon = (eventType: TimelineEvent['eventType']) => {
    switch (eventType) {
      case 'created':
        return <Plus className="w-5 h-5 text-blue-600" />;
      case 'status_change':
        return <ArrowRight className="w-5 h-5 text-orange-600" />;
      case 'approved':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case 'rejected':
        return <XCircle className="w-5 h-5 text-red-600" />;
      case 'disbursed':
        return <DollarSign className="w-5 h-5 text-green-600" />;
      case 'comment_added':
        return <MessageCircle className="w-5 h-5 text-blue-600" />;
      case 'document_uploaded':
        return <FileText className="w-5 h-5 text-purple-600" />;
      case 'assignment_changed':
        return <UserCheck className="w-5 h-5 text-indigo-600" />;
      default:
        return <Clock className="w-5 h-5 text-gray-600" />;
    }
  };

  const getEventColor = (eventType: TimelineEvent['eventType']) => {
    switch (eventType) {
      case 'created':
        return 'border-blue-200 bg-blue-50';
      case 'status_change':
        return 'border-orange-200 bg-orange-50';
      case 'approved':
        return 'border-green-200 bg-green-50';
      case 'rejected':
        return 'border-red-200 bg-red-50';
      case 'disbursed':
        return 'border-green-200 bg-green-50';
      case 'comment_added':
        return 'border-blue-200 bg-blue-50';
      case 'document_uploaded':
        return 'border-purple-200 bg-purple-50';
      case 'assignment_changed':
        return 'border-indigo-200 bg-indigo-50';
      default:
        return 'border-gray-200 bg-gray-50';
    }
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatRelativeTime = (date: Date | string) => {
    const now = new Date();
    const eventDate = new Date(date);
    const diffInHours = Math.floor((now.getTime() - eventDate.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) {
      return 'Just now';
    } else if (diffInHours < 24) {
      return `${diffInHours} hour${diffInHours > 1 ? 's' : ''} ago`;
    } else {
      const diffInDays = Math.floor(diffInHours / 24);
      return `${diffInDays} day${diffInDays > 1 ? 's' : ''} ago`;
    }
  };

  if (timeline.length === 0) {
    return (
      <div className="text-center py-12">
        <Clock className="w-16 h-16 text-gray-300 mx-auto mb-4" />
        <h3 className="text-lg font-medium text-gray-500 mb-2">No Timeline Events</h3>
        <p className="text-gray-400">Timeline events will appear here as the request progresses.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-2 mb-6">
        <Clock className="w-5 h-5 text-gray-600" />
        <h3 className="text-lg font-semibold" style={{ color: '#323E48' }}>
          Request Timeline ({timeline.length} events)
        </h3>
      </div>

      <div className="relative">
        {/* Timeline Line */}
        <div className="absolute left-6 top-0 bottom-0 w-0.5 bg-gray-200"></div>

        {/* Timeline Events */}
        <div className="space-y-6">
          {timeline.map((event, index) => (
            <motion.div
              key={event.id}
              className="relative flex items-start space-x-4"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
            >
              {/* Event Icon */}
              <div className={`relative z-10 flex items-center justify-center w-12 h-12 rounded-full border-2 ${getEventColor(event.eventType)}`}>
                {getEventIcon(event.eventType)}
              </div>

              {/* Event Content */}
              <div className="flex-1 min-w-0 pb-6">
                <motion.div
                  className="bg-white rounded-2xl border border-gray-200 p-4 shadow-sm"
                  whileHover={{ scale: 1.01, y: -2 }}
                  transition={{ duration: 0.2 }}
                >
                  {/* Event Header */}
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex items-center space-x-3">
                      <div className="flex items-center space-x-2">
                        <div className="w-8 h-8 rounded-full bg-blue-100 flex items-center justify-center text-sm">
                          {event.userAvatar || <User className="w-4 h-4 text-blue-600" />}
                        </div>
                        <div>
                          <h4 className="font-medium text-gray-900">{event.title}</h4>
                          <p className="text-sm text-gray-600">{event.userName}</p>
                        </div>
                      </div>
                    </div>

                    <div className="text-right">
                      <p className="text-sm text-gray-500">{formatRelativeTime(event.createdAt)}</p>
                      <p className="text-xs text-gray-400">{formatDate(event.createdAt)}</p>
                    </div>
                  </div>

                  {/* Event Description */}
                  <p className="text-gray-700 mb-3">{event.description}</p>

                  {/* Status Change Details */}
                  {event.eventType === 'status_change' && event.previousValue && event.newValue && (
                    <div className="flex items-center space-x-2 text-sm">
                      <span className="px-2 py-1 bg-red-100 text-red-800 rounded-full">
                        {event.previousValue}
                      </span>
                      <ArrowRight className="w-4 h-4 text-gray-400" />
                      <span className="px-2 py-1 bg-green-100 text-green-800 rounded-full">
                        {event.newValue}
                      </span>
                    </div>
                  )}

                  {/* Assignment Change Details */}
                  {event.eventType === 'assignment_changed' && event.previousValue && event.newValue && (
                    <div className="flex items-center space-x-2 text-sm">
                      <span className="text-gray-600">From:</span>
                      <span className="px-2 py-1 bg-gray-100 text-gray-800 rounded-full">
                        {event.previousValue}
                      </span>
                      <ArrowRight className="w-4 h-4 text-gray-400" />
                      <span className="text-gray-600">To:</span>
                      <span className="px-2 py-1 bg-blue-100 text-blue-800 rounded-full">
                        {event.newValue}
                      </span>
                    </div>
                  )}

                  {/* Metadata Display */}
                  {event.metadata && Object.keys(event.metadata).length > 0 && (
                    <div className="mt-3 p-3 bg-gray-50 rounded-lg">
                      <h5 className="text-sm font-medium text-gray-700 mb-2">Additional Details:</h5>
                      <div className="space-y-1">
                        {Object.entries(event.metadata).map(([key, value]) => (
                          <div key={key} className="flex justify-between text-sm">
                            <span className="text-gray-600 capitalize">{key.replace('_', ' ')}:</span>
                            <span className="text-gray-900 font-medium">{String(value)}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </motion.div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Timeline End Marker */}
        <div className="relative flex items-center justify-center">
          <div className="w-4 h-4 bg-gray-300 rounded-full border-2 border-white shadow-sm"></div>
        </div>
      </div>

      {/* Timeline Summary */}
      <motion.div
        className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-6 border border-blue-200"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3, delay: 0.5 }}
      >
        <h4 className="font-medium text-blue-900 mb-3">Timeline Summary</h4>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-blue-600">
              {timeline.filter(e => e.eventType === 'status_change').length}
            </div>
            <div className="text-sm text-blue-700">Status Changes</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-green-600">
              {timeline.filter(e => e.eventType === 'comment_added').length}
            </div>
            <div className="text-sm text-green-700">Comments</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-purple-600">
              {timeline.filter(e => e.eventType === 'document_uploaded').length}
            </div>
            <div className="text-sm text-purple-700">Documents</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-indigo-600">
              {timeline.filter(e => e.eventType === 'assignment_changed').length}
            </div>
            <div className="text-sm text-indigo-700">Assignments</div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default TimelineComponent;
