import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  X,
  Calendar,
  DollarSign,
  MapPin,
  User,
  Clock,
  MessageCircle,
  FileText,
  Activity,
  Eye,
  AlertCircle,
  CheckCircle,
  XCircle,
  Pause
} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { WithdrawalRequest, RequestDetails, RequestDetailsModalState } from '../types/withdrawalTypes';
import { withdrawalRequestService } from '../services/withdrawalRequestService';
import { commentService } from '../services/commentService';
import { timelineService } from '../services/timelineService';
import CommentSystem from './CommentSystem';
import TimelineComponent from './TimelineComponent';
import RequestTrackingDashboard from './RequestTrackingDashboard';
import HorizontalTimelineComponent from './HorizontalTimelineComponent';
import EnhancedOverviewSection from './EnhancedOverviewSection';
import EnhancedCommentSystem from './EnhancedCommentSystem';
import EnhancedDocumentsSection from './EnhancedDocumentsSection';

interface RequestDetailsModalProps {
  isOpen: boolean;
  request: WithdrawalRequest | null;
  onClose: () => void;
}

const RequestDetailsModal: React.FC<RequestDetailsModalProps> = ({
  isOpen,
  request,
  onClose
}) => {
  const { user } = useAuth();
  const [modalState, setModalState] = useState<RequestDetailsModalState>({
    isOpen: false,
    requestId: null,
    activeTab: 'overview',
    isLoading: false
  });
  const [requestDetails, setRequestDetails] = useState<RequestDetails | null>(null);
  const [showModal, setShowModal] = useState(false);

  // Load request details when modal opens
  useEffect(() => {
    if (isOpen && request) {
      setModalState(prev => ({ ...prev, isLoading: true }));
      loadRequestDetails(request.id);
    }
  }, [isOpen, request]);

  const loadRequestDetails = async (id: string) => {
    try {
      const request = withdrawalRequestService.getRequestById(id);
      if (!request) {
        console.error('Request not found:', id);
        return;
      }

      const comments = commentService.getCommentsByRequestId(id);
      const timeline = timelineService.getTimelineByRequestId(id);
      const timelineStats = timelineService.getTimelineStats(id);

      const details: RequestDetails = {
        ...request,
        comments,
        timeline,
        documents: [], // TODO: Implement document service
        totalComments: comments.length,
        lastActivity: timelineStats.lastActivity || request.updatedAt
      };

      setRequestDetails(details);
    } catch (error) {
      console.error('Error loading request details:', error);
    } finally {
      setModalState(prev => ({ ...prev, isLoading: false }));
    }
  };

  const handleClose = () => {
    setRequestDetails(null);
    onClose();
  };

  useEffect(() => {
  const handleKeyDown = (e: KeyboardEvent) => {
    if (e.key === 'Escape') {
      handleClose();
    }
  };

  document.addEventListener('keydown', handleKeyDown);
  return () => {
    document.removeEventListener('keydown', handleKeyDown);
  };
}, []);

  const handleTabChange = (tab: RequestDetailsModalState['activeTab']) => {
    setModalState(prev => ({ ...prev, activeTab: tab }));
  };

  const getStatusIcon = (status: string) => {
    switch (status.toLowerCase()) {
      case 'disbursed':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case 'rejected':
        return <XCircle className="w-5 h-5 text-red-600" />;
      case 'pending':
        return <Clock className="w-5 h-5 text-yellow-600" />;
      default:
        return <Pause className="w-5 h-5 text-blue-600" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'disbursed':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'rejected':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      default:
        return 'bg-blue-100 text-blue-800 border-blue-200';
    }
  };

  const formatCurrency = (amount: number, currency: string) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!isOpen || !requestDetails) {
    return null;
  }

  return (
    <AnimatePresence>
      {isOpen && requestDetails && (
        <motion.div
          className="fixed inset-0 bg-black/60 backdrop-blur-md flex items-start justify-center p-4 pt-8 z-[9999]"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={(e) => {
            if (e.target === e.currentTarget) {
              handleClose();
            }
          }}
          style={{ overflow: 'hidden' }}
        >
          <motion.div
            className="bg-white rounded-3xl w-full max-w-7xl max-h-[95vh] overflow-hidden"
            style={{
              border: '1px solid #DEE1E3',
              boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25), 0 0 0 1px rgba(255, 255, 255, 0.05)',
              background: 'linear-gradient(135deg, #ffffff 0%, #fafbfc 100%)'
            }}
            initial={{ opacity: 0, y: 60, scale: 0.9, rotateX: 15 }}
            animate={{ opacity: 1, y: 0, scale: 1, rotateX: 0 }}
            exit={{ opacity: 0, y: 60, scale: 0.9, rotateX: 15 }}
            transition={{
              duration: 0.4,
              ease: [0.16, 1, 0.3, 1],
              staggerChildren: 0.1
            }}
          >
            {/* 🔼 Everything inside this motion.div stays exactly as you have it */}
            {/* That includes the header, close button, tab bar, and tab content */}

            {/* Just be sure your close button calls this: */}
            <motion.button
              onClick={handleClose}
              className="p-3 rounded-full transition-all duration-300 flex items-center justify-center shadow-lg backdrop-blur-xl border border-white/30"
              title="Close"
              whileHover={{ scale: 1.15, rotate: 90 }}
              whileTap={{ scale: 0.9 }}
              style={{
                background: 'linear-gradient(145deg, #f87171, #ef4444)',
                boxShadow: '0 4px 20px rgba(239, 68, 68, 0.4)',
              }}
            >
              <X className="w-5 h-5 text-white" />
            </motion.button>

            {/* ✅ Everything else (tabs and content) remains as you already wrote it */}
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>

  );
};

export default RequestDetailsModal;
